import React, { Component } from "react";
import Inputs from "./components/inputs.jsx";
import "./App.css";

let newKey = 2;

class App extends Component {
  state = {
    inputs: [
      {
        id: 0,
        annotation1: "",
        lifecycle1: "",
        annotation2: "",
        lifecycle2: "",
        relation: "",
      },
    ],
  };

  handleChange = (inputValue, rowIndex, fieldName) => {
    const inputs = [...this.state.inputs];
    inputs[rowIndex][fieldName] = inputValue;
    this.setState({ inputs });
    console.log({ inputValue });
  };

  handleAdd = () => {
    console.log("Added an input");
    newKey++;
    const newInput = {
      id: newKey,
      annotation1: "",
      lifecycle1: "",
      annotation2: "",
      lifecycle2: "",
      relation: "",
    };
    const inputs = [...this.state.inputs].concat(newInput);
    // console.log(inputs[newKey].id);
    this.setState({ inputs });
  };

  handleDelete = (inputId) => {
    console.log(inputId);
    const inputs = this.state.inputs.filter((c) => c.id !== inputId);
    this.setState({ inputs });
  };

  handleReset = () => {
    window.location.reload(false);
  };

  render() {
    return (
      <div>
        <h1 className="App-header">QUERY BUILDER</h1>
        <main className="Main">
          <Inputs
            inputs={this.state.inputs}
            onAdd={this.handleAdd}
            onDelete={this.handleDelete}
            onReset={this.handleReset}
            onChange={this.handleChange}
          />
        </main>
      </div>
    );
  }
}

export default App;
