import React, { Component } from "react";
import Inputs from "./components/inputs.jsx";
import "./App.css";

class App extends Component {
  state = {
    inputs: [
      { id: 0, value: "Random" },
      { id: 1, value: "Random" },
      { id: 2, value: "Random" },
    ],
  };

  constructor() {
    super();
  }

  handleAdd = () => {
    console.log("Added an input");
    const newInput = { id: newKey + 1, value: "Random" };
    const inputs = [...this.state.inputs].concat(newInput);
    console.log(inputs);
    this.setState({ inputs });
  };

  handleDelete = (inputId) => {
    console.log(inputId);
    const inputs = this.state.inputs.filter((c) => c.id !== inputId);
    this.setState({ inputs });
  };

  handleReset = () => {
    window.location.reload(false);
  };

  render() {
    return (
      <div>
        <h1 className="App-header">NEANEX QUERY BUILDER</h1>
        <main className="Main">
          <Inputs
            inputs={this.state.inputs}
            onAdd={this.handleAdd}
            onDelete={this.handleDelete}
            onReset={this.handleReset}
          />
        </main>
      </div>
    );
  }
}

export default App;
